template<std::size_t SIZE>
vector<bitset<24>> code_head_tail(array<int, SIZE> &code)
{
    string head_str = "", tail_str = "";

    for (int i = 0; i < 24; i++) {
        if (code[i] == 1)
            head_str += "1";
        else
            head_str += "0";    
    }

    int s = code.size();
    for (int i = s-24; i < s; i++) {
        if (code[i] == 1)
            tail_str += "1";
        else
            tail_str += "0";    
    }

    return {bitset<24>{head_str}, bitset<24>{tail_str}};
}

